window.onload = function (e) {
    liff.init(function (data) {
        initializeApp(data);
    });
};

function initializeApp(data) {
     //取得QueryString
            let urlParams = new URLSearchParams(window.location.search);
    //顯示QueryString
            document.getElementById('QueryString').textContent = urlParams.toString();
    //顯示UserId        
            document.getElementById('userid').textContent = data.context.userId;
    // work call
    document.getElementById('ButtonGetProfile').addEventListener('click', function () {
    //取得User Proile
            liff.getProfile().then(
                profile=> {
                    //顯示在text box中
                    $('#UserInfo').val(profile.displayName);
                    //居然可以alert
                    alert('done');
                }
            );
    });
    

    // sendMessages call
    document.getElementById('ButtonSendMsg').addEventListener('click', function () {
        liff.sendMessages([{
            type: 'text',
            text: "You've successfully sent a message! Hooray!"
        }, {
            type: 'sticker',
            packageId: '2',
            stickerId: '144'
        }]).then(function () {
            window.alert("Message sent");
        }).catch(function (error) {
            window.alert("Error sending message: " + error);
        });
    });

}